s = input()
L = len(s)

dp = [[2500 for j in range(L)] for i in range(L)]

for i in range(L):
    dp[i][i] = 1

pel_end = set()

for j in range(1, L):
    for i in range(j - 1, -1, -1):
        subs = s[i : j + 1]
        if subs == subs[::-1]:
            dp[i][j] = 1
            pel_end.add(j)
        else:
            dp[i][j] = min(dp[i + 1][j], dp[i][j - 1]) + 1

    for k in pel_end:
        if k >= j:
            break
        dp[0][j] = min(dp[0][j], dp[0][k] + dp[k + 1][j])

print(dp[0][L - 1])


